INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (870, 'XELBYvtp78Nh8R1-h9OoPw', '66fda2e27e08dc03b5bb3d6e', 'United States', 'us', 'Cali three stripes', '<p>Utility poles with 3 yellow stripes are most commonly found in California.</p>', 'Check out  <a href="https://www.plonkit.net/united-states" rel="nofollow" target="_blank">www.plonkit.net/united-states</a> for more clues.', '["https://learnablemeta.com/images/zEJeHYNKrUMWRzhFkuWe7NbnCLk6Fl3n7Znd.avif"]', '{"country":"United States","metaName":"Cali three stripes","note":"<p>Utility poles with 3 yellow stripes are most commonly found in California.</p>","images":["https://learnablemeta.com/images/zEJeHYNKrUMWRzhFkuWe7NbnCLk6Fl3n7Znd.avif"],"footer":"Check out  <a href=\"https://www.plonkit.net/united-states\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/united-states</a> for more clues."}', '2025-09-01 09:25:33', '2025-09-01 09:25:33');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (871, '6b1_psIpyf00XvMsIhPDhg', '66fda2e27e08dc03b5bb3d6e', 'Japan', 'jp', 'Chugoku pole top', '<p>This pole top is unique to Chugoku, there is similar version that are more thicc so make sure the pole top is thin like the one on the image.</p>', 'Check out  <a href="https://www.plonkit.net/japan" rel="nofollow" target="_blank">www.plonkit.net/japan</a> for more clues.', '["https://learnablemeta.com/images/1/vZ4HhmLGGJ8jz9ba8Fq4VV1GprSbhaJDuDud.avif","https://learnablemeta.com/images/1/3RkU8lcC2SJw005sUnDMLQBU7afO1Cb23wUf.avif"]', '{"country":"Japan","metaName":"Chugoku pole top","note":"<p>This pole top is unique to Chugoku, there is similar version that are more thicc so make sure the pole top is thin like the one on the image.</p>","images":["https://learnablemeta.com/images/1/vZ4HhmLGGJ8jz9ba8Fq4VV1GprSbhaJDuDud.avif","https://learnablemeta.com/images/1/3RkU8lcC2SJw005sUnDMLQBU7afO1Cb23wUf.avif"],"footer":"Check out  <a href=\"https://www.plonkit.net/japan\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/japan</a> for more clues."}', '2025-09-01 09:25:57', '2025-09-01 09:25:57');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (872, '5h6GiVCZVN_WH_plqjbTrQ', '66fda2e27e08dc03b5bb3d6e', 'Jordan', 'jo', 'Black car meta', '<p>While driving on the right side, yellow outer lines with white middle lines are commonly found in Jordan, Israel, and the UAE. The black back of the Google car without an antenna can help confirm you’re in Jordan 100%.</p>', 'Check out  <a href="https://www.plonkit.net/jordan" rel="nofollow" target="_blank">www.plonkit.net/jordan</a> for more clues.', '["https://learnablemeta.com/images/1nIlCPbAAeyPrNrBrlbuWWTZjDTv6KM3J0yo.avif"]', '{"country":"Jordan","metaName":"Black car meta","note":"<p>While driving on the right side, yellow outer lines with white middle lines are commonly found in Jordan, Israel, and the UAE. The black back of the Google car without an antenna can help confirm you’re in Jordan 100%.</p>","images":["https://learnablemeta.com/images/1nIlCPbAAeyPrNrBrlbuWWTZjDTv6KM3J0yo.avif"],"footer":"Check out  <a href=\"https://www.plonkit.net/jordan\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/jordan</a> for more clues."}', '2025-09-01 09:26:47', '2025-09-01 09:26:47');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (873, 'eKUaix6xfpielRRN5PUC-g', '66fda2e27e08dc03b5bb3d6e', 'Japan', 'jp', 'Kansai reflector', '<p>Tall reflector with washed colors is exclusive to Kansai region.</p>', 'Check out  <a href="https://www.plonkit.net/japan" rel="nofollow" target="_blank">www.plonkit.net/japan</a> for more clues.', '["https://learnablemeta.com/images/2AqmRg6tGF9qp75C9SVOJ9fhThcaFffddD4G.avif","https://learnablemeta.com/images/1/3RkU8lcC2SJw005sUnDMLQBU7afO1Cb23wUf.avif"]', '{"country":"Japan","metaName":"Kansai reflector","note":"<p>Tall reflector with washed colors is exclusive to Kansai region.</p>","images":["https://learnablemeta.com/images/2AqmRg6tGF9qp75C9SVOJ9fhThcaFffddD4G.avif","https://learnablemeta.com/images/1/3RkU8lcC2SJw005sUnDMLQBU7afO1Cb23wUf.avif"],"footer":"Check out  <a href=\"https://www.plonkit.net/japan\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/japan</a> for more clues."}', '2025-09-01 09:27:14', '2025-09-01 09:27:14');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (875, 'HP6tWeeojf7KDHLw7gg-8Q', '66fda2e27e08dc03b5bb3d6e', 'Brazil', 'br', 'Maranhão wide insulators', '<p>Poles with these <strong>wide insulators in a trident</strong> formation are common in Maranhão. The insulator itself is common across many states, but not in this configuration.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/brazil" rel="nofollow" target="_blank">www.plonkit.net/brazil</a>.', '["https://learnablemeta.com/images/40/qZ4kVIcbWQ74vw7184BRgKVtIgrAhr7NsgfH.avif"]', '{"country":"Brazil","metaName":"Maranhão wide insulators","note":"<p>Poles with these <strong>wide insulators in a trident</strong> formation are common in Maranhão. The insulator itself is common across many states, but not in this configuration.</p>","images":["https://learnablemeta.com/images/40/qZ4kVIcbWQ74vw7184BRgKVtIgrAhr7NsgfH.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/brazil\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/brazil</a>."}', '2025-09-01 09:27:55', '2025-09-01 09:27:55');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (876, 'A4B9r553TIiU-x_Ft8BtHg', '66fda2e27e08dc03b5bb3d6e', 'New Zealand', 'nz', 'Timaru orange sticker', '<p>Poles near Timaru often have a <strong>tall rectangle of orange paint</strong> on them with the text “DANGER LIVE WIRES”.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/new-zealand" rel="nofollow" target="_blank">www.plonkit.net/new-zealand</a>.', '["https://learnablemeta.com/images/1/1754129182232-9Rl.avif"]', '{"country":"New Zealand","metaName":"Timaru orange sticker","note":"<p>Poles near Timaru often have a <strong>tall rectangle of orange paint</strong> on them with the text “DANGER LIVE WIRES”.</p>","images":["https://learnablemeta.com/images/1/1754129182232-9Rl.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/new-zealand\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/new-zealand</a>."}', '2025-09-01 09:28:29', '2025-09-01 09:28:29');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (877, 'rP7JzMom66p1w4-icGQqNg', '67d4b7356d77cefed0f90e35', 'Cambodia', 'kh', 'Southeast Light Soil', '<p>Sandy, white soil is very common in the region southeast of Phnom Penh, as well as around the city itself. The colour can range from bright white to slightly beige.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/cambodia" rel="nofollow" target="_blank">www.plonkit.net/cambodia</a>.', '["https://learnablemeta.com/images/664/Fg53k6ERrcIUkmYwSxyxejdLlEVXw9Kl55ri.avif"]', '{"country":"Cambodia","metaName":"Southeast Light Soil","note":"<p>Sandy, white soil is very common in the region southeast of Phnom Penh, as well as around the city itself. The colour can range from bright white to slightly beige.</p>","images":["https://learnablemeta.com/images/664/Fg53k6ERrcIUkmYwSxyxejdLlEVXw9Kl55ri.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/cambodia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/cambodia</a>."}', '2025-09-10 07:34:29', '2025-09-10 07:34:29');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (878, '4A0wJT-hzwPao5KJypfXRg', '67d4b7356d77cefed0f90e35', 'India', 'in', 'Gujarat Salt Flats', '<p>Salt flats and coastal marshes with nearly no vegetation, other than short yellow or brown grass, can be found in parts of Gujarat. Shallow bodies of water can often be seen around these areas.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (879, 'AVOZOFY5ELJ5EFzvn0b8zA', '67d4b7356d77cefed0f90e35', 'Bolivia', 'bo', 'Chile Border Desert', '<p>Between the villages of Villa Catalina and Coipasa, there is an isolated stretch of coverage where the Google car drove through extremely arid desert terrain. This small road is located close to the southwestern end of Ruta 12, near the Chilean border. It can be hard to spot on the map, because it blends in with the salt flat.
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (880, 'lkwLaN6QgggX9-D71jieSw', '67d4b7356d77cefed0f90e35', 'Spain', 'es', 'Castilla La Mancha', '<p>Castilla la Mancha can have completely flat landscapes. Half of Spain’s wine is produced in this region, so vineyards are fairly common.
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (881, 'Axp0zg09OY9TqqPdXMV8Iw', '67d4b7356d77cefed0f90e35', 'Brazil', 'br', 'North Roraima Mountains', '<p>The landscape near the town of Uiramuta, in northern Roraima, features a very distinct landscape. It is dry, with large mountains containing numerous “folds.”</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/brazil" rel="nofollow" target="_blank">www.plonkit.net/brazil</a>.', '["https://learnablemeta.com/images/664/SSlR159r5tZooD0qJOvvi1WhLPEj7WAS6kM1.avif"]', '{"country":"Brazil","metaName":"North Roraima Mountains","note":"<p>The landscape near the town of Uiramuta, in northern Roraima, features a very distinct landscape. It is dry, with large mountains containing numerous “folds.”</p>","images":["https://learnablemeta.com/images/664/SSlR159r5tZooD0qJOvvi1WhLPEj7WAS6kM1.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/brazil\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/brazil</a>."}', '2025-09-11 08:34:35', '2025-09-11 08:34:35');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (882, 'XI6CgXRRLdnjvN9lQKIsjg', '67d4b7356d77cefed0f90e35', 'Slovakia', 'sk', 'Mountains', '<p>Two wide valleys surrounded by tall, often sharp mountains can be found in the northern parts of the country.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (884, 'g4IvMAuRsQC-zBH6e42GEw', '67d4b7356d77cefed0f90e35', 'Albania', 'al', 'Theth National Park', '<p>The road leading into Theth National Park can typically be recognised by the tall, jagged mountains, often with bare mountainsides.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/albania" rel="nofollow" target="_blank">www.plonkit.net/albania</a>.', '["https://learnablemeta.com/images/664/b3N1KWkH1IY0rNKROzq4FKuH5VUpbWOhuCQO.avif"]', '{"country":"Albania","metaName":"Theth National Park","note":"<p>The road leading into Theth National Park can typically be recognised by the tall, jagged mountains, often with bare mountainsides.</p>","images":["https://learnablemeta.com/images/664/b3N1KWkH1IY0rNKROzq4FKuH5VUpbWOhuCQO.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/albania\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/albania</a>."}', '2025-09-11 08:40:31', '2025-09-11 08:40:31');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (885, 'WlmIO1qCINUJqlqe-pZqpw', '67d4b7356d77cefed0f90e35', 'Norway', 'no', 'Far North Grassland', '<p>Coastal grasslands with little to no other vegetation and only somewhat low, distant mountains can be found by coastal roads in the far north.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/norway" rel="nofollow" target="_blank">www.plonkit.net/norway</a>.', '["https://learnablemeta.com/images/664/pMGnJDHW6pwOEch6TCVNgAJPf6ws730wOfTr.avif"]', '{"country":"Norway","metaName":"Far North Grassland","note":"<p>Coastal grasslands with little to no other vegetation and only somewhat low, distant mountains can be found by coastal roads in the far north.</p>","images":["https://learnablemeta.com/images/664/pMGnJDHW6pwOEch6TCVNgAJPf6ws730wOfTr.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/norway\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/norway</a>."}', '2025-09-11 08:40:54', '2025-09-11 08:40:54');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (886, 'rDVcZ2BjwsG4D3zzJhUhpQ', '67d4b7356d77cefed0f90e35', 'Panama', 'pa', 'David Fields', '<p>The area around David is very flat, with lots of fairly open fields. Usually, mountains will be visible to the north and northeast.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/panama" rel="nofollow" target="_blank">www.plonkit.net/panama</a>.', '["https://learnablemeta.com/images/664/Fm9m2QG57s5pFR96Br4dy7vxPwdOSwxTqsvV.avif"]', '{"country":"Panama","metaName":"David Fields","note":"<p>The area around David is very flat, with lots of fairly open fields. Usually, mountains will be visible to the north and northeast.</p>","images":["https://learnablemeta.com/images/664/Fm9m2QG57s5pFR96Br4dy7vxPwdOSwxTqsvV.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/panama\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/panama</a>."}', '2025-09-11 08:42:46', '2025-09-11 08:42:46');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (887, 'boqF53iorCD2-WvPb1wUKg', '67d4b7356d77cefed0f90e35', 'Israel', 'il', 'West Bank', '<p>Most of the West Bank has a semi-arid Mediterranean landscape. It is generally greener than southern Israel, drier than northern Israel and west of Jerusalem, and hillier and less agricultural than central Israel.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/israel-west-bank" rel="nofollow" target="_blank">www.plonkit.net/israel-west-bank</a>.', '["https://learnablemeta.com/images/664/0uF2YUqhY6VUAJvBCPzxix9sjr0KSpnTSjUb.avif"]', '{"country":"Israel","metaName":"West Bank","note":"<p>Most of the West Bank has a semi-arid Mediterranean landscape. It is generally greener than southern Israel, drier than northern Israel and west of Jerusalem, and hillier and less agricultural than central Israel.</p>","images":["https://learnablemeta.com/images/664/0uF2YUqhY6VUAJvBCPzxix9sjr0KSpnTSjUb.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/israel-west-bank\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/israel-west-bank</a>."}', '2025-09-12 09:21:50', '2025-09-12 09:21:50');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (888, '9bxbU7grHvl-R2ODWseOGQ', '67d4b7356d77cefed0f90e35', 'Chile', 'cl', 'Santiago Landscape', '<p>The region around Santiago can be recognized by its semi-arid climate, having dry hills and scattered vegetation.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/chile" rel="nofollow" target="_blank">www.plonkit.net/chile</a>.', '["https://learnablemeta.com/images/664/lTyYdBfw08zICYue1RISa09uEibQ1HgKOS8R.avif"]', '{"country":"Chile","metaName":"Santiago Landscape","note":"<p>The region around Santiago can be recognized by its semi-arid climate, having dry hills and scattered vegetation.</p>","images":["https://learnablemeta.com/images/664/lTyYdBfw08zICYue1RISa09uEibQ1HgKOS8R.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/chile\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/chile</a>."}', '2025-09-12 09:23:46', '2025-09-12 09:23:46');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (889, 'wEuve5bd0-9GuyFVFolj1Q', '67d4b7356d77cefed0f90e35', 'Thailand', 'th', 'Mukdahan', '<p>The province of Mukdahan features many long, low hills among fields of rice, sugarcane, cassava and rubber trees.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/thailand" rel="nofollow" target="_blank">www.plonkit.net/thailand</a>.', '["https://learnablemeta.com/images/664/XQdsowGUmb7J6pTeHTMHbZgzAaaEl3qH9Z5Q.avif"]', '{"country":"Thailand","metaName":"Mukdahan","note":"<p>The province of Mukdahan features many long, low hills among fields of rice, sugarcane, cassava and rubber trees.</p>","images":["https://learnablemeta.com/images/664/XQdsowGUmb7J6pTeHTMHbZgzAaaEl3qH9Z5Q.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/thailand\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/thailand</a>."}', '2025-09-12 09:24:19', '2025-09-12 09:24:19');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (890, 'bXKpSqwGx237b_hQUyrVLQ', '67d4b7356d77cefed0f90e35', 'Malaysia', 'my', 'Perlis Rocks', '<p>These isolated rock formations, rising abruptly from an otherwise flat landscape, can be found near the Thai border in Perlis state.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/malaysia" rel="nofollow" target="_blank">www.plonkit.net/malaysia</a>.', '["https://learnablemeta.com/images/664/h7gqqPNWNVkejEbY6hy9JsYfsRJZGgDnLUdM.avif"]', '{"country":"Malaysia","metaName":"Perlis Rocks","note":"<p>These isolated rock formations, rising abruptly from an otherwise flat landscape, can be found near the Thai border in Perlis state.</p>","images":["https://learnablemeta.com/images/664/h7gqqPNWNVkejEbY6hy9JsYfsRJZGgDnLUdM.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/malaysia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/malaysia</a>."}', '2025-09-12 09:31:38', '2025-09-12 09:31:38');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (891, 'RKg6K43sWdsLrpUXm5PiEQ', '67d4b7356d77cefed0f90e35', 'Sweden', 'se', 'Mountains', '<p>While it is possible to see forested mountains in several regions in the country, mountains with no trees at the top can only be found in the northwestern parts of the country.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/sweden" rel="nofollow" target="_blank">www.plonkit.net/sweden</a>.', '["https://learnablemeta.com/images/664/BUwTMfcuxJWpBSKcq4wvsM1StYqEL2LJb7P9.avif"]', '{"country":"Sweden","metaName":"Mountains","note":"<p>While it is possible to see forested mountains in several regions in the country, mountains with no trees at the top can only be found in the northwestern parts of the country.</p>","images":["https://learnablemeta.com/images/664/BUwTMfcuxJWpBSKcq4wvsM1StYqEL2LJb7P9.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/sweden\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/sweden</a>."}', '2025-09-12 09:32:00', '2025-09-12 09:32:00');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (892, '8vyDX55KFijcyBvk3lLgmw', '67d4b7356d77cefed0f90e35', 'Indonesia', 'id', 'Palu Bay', '<p>Palu Bay is a unique slightly north-west facing bay just north of Palu city, which features mountains to the west of the bay, and a flat, more urbanised landscape to the east.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/indonesia" rel="nofollow" target="_blank">www.plonkit.net/indonesia</a>.', '["https://learnablemeta.com/images/664/IYyx1nFdjUcwDvl1lST3CquQFfZjK6LZWKp0.avif"]', '{"country":"Indonesia","metaName":"Palu Bay","note":"<p>Palu Bay is a unique slightly north-west facing bay just north of Palu city, which features mountains to the west of the bay, and a flat, more urbanised landscape to the east.</p>","images":["https://learnablemeta.com/images/664/IYyx1nFdjUcwDvl1lST3CquQFfZjK6LZWKp0.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/indonesia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/indonesia</a>."}', '2025-09-12 09:37:38', '2025-09-12 09:37:38');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (893, 'MQMlu_nXxEiAyjtIowGLvg', '67d4b7356d77cefed0f90e35', 'Uganda', 'ug', 'Crater', '<p>On the northern coverage, it is common to see large, water-filled craters. Similar craters cannot be found elsewhere in the country.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (894, 'h00bCK_7RHzzsu7Vy_Zz0Q', '67d4b7356d77cefed0f90e35', 'Turkey', 'tr', 'Lake Van', '<p>Lake Van is by far the biggest lake in Turkey and has a semi-arid landscape, being surrounded by dry grassy hills and occasional trees and agricultural fields near the shore.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/turkey" rel="nofollow" target="_blank">www.plonkit.net/turkey</a>.', '["https://learnablemeta.com/images/664/j8EFzVr9pxEL5buzt1zQnnvt8Qq0il7dx9Lj.avif"]', '{"country":"Turkey","metaName":"Lake Van","note":"<p>Lake Van is by far the biggest lake in Turkey and has a semi-arid landscape, being surrounded by dry grassy hills and occasional trees and agricultural fields near the shore.</p>","images":["https://learnablemeta.com/images/664/j8EFzVr9pxEL5buzt1zQnnvt8Qq0il7dx9Lj.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/turkey\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/turkey</a>."}', '2025-09-12 09:38:47', '2025-09-12 09:38:47');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (895, 'hr0IduICBWRP0_VHrAkGsw', '67d4b7356d77cefed0f90e35', 'Iceland', 'is', 'Eiðar Valley', '<p>The wide valley around Eiðar is slightly hilly and agricultural, with tall, snowy peaks visible in the distance on either side, to the east and northwest, respectively.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/iceland" rel="nofollow" target="_blank">www.plonkit.net/iceland</a>.', '["https://learnablemeta.com/images/664/X6C222UREExFJBtg8m4cPP65UqXhaEPewYIo.avif"]', '{"country":"Iceland","metaName":"Eiðar Valley","note":"<p>The wide valley around Eiðar is slightly hilly and agricultural, with tall, snowy peaks visible in the distance on either side, to the east and northwest, respectively.</p>","images":["https://learnablemeta.com/images/664/X6C222UREExFJBtg8m4cPP65UqXhaEPewYIo.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/iceland\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/iceland</a>."}', '2025-09-12 09:39:16', '2025-09-12 09:39:16');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (896, 'r-4fX5pqLIr38rlKHOn4EQ', '67d4b7356d77cefed0f90e35', 'Nigeria', 'ng', 'Niger', '<p>Niger state is the shitcam equivalent to Benue state. It is almost entirely covered by shitcam, and can be recognized by the scattered trees, reddish soil, and dry grass.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/nigeria" rel="nofollow" target="_blank">www.plonkit.net/nigeria</a>.', '["https://learnablemeta.com/images/664/4DNSfLElJQEQYnyxdxE6VGfL3m0p0AfPqhDX.avif"]', '{"country":"Nigeria","metaName":"Niger","note":"<p>Niger state is the shitcam equivalent to Benue state. It is almost entirely covered by shitcam, and can be recognized by the scattered trees, reddish soil, and dry grass.</p>","images":["https://learnablemeta.com/images/664/4DNSfLElJQEQYnyxdxE6VGfL3m0p0AfPqhDX.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/nigeria\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/nigeria</a>."}', '2025-09-12 09:39:49', '2025-09-12 09:39:49');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (897, 'M8uBvvbG3pPsP3wGFRz1Tg', '67d4b7356d77cefed0f90e35', 'Puerto Rico', 'pr', 'Southern Dryness', '<p>Southern Puerto Rico can often look distinctly dryer than the rest of the island.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/puerto-rico" rel="nofollow" target="_blank">www.plonkit.net/puerto-rico</a>.', '["https://learnablemeta.com/images/664/yQ4vM1Gp7OKrCgZalSZjosyt9wDT7uCINOIK.avif"]', '{"country":"Puerto Rico","metaName":"Southern Dryness","note":"<p>Southern Puerto Rico can often look distinctly dryer than the rest of the island.</p>","images":["https://learnablemeta.com/images/664/yQ4vM1Gp7OKrCgZalSZjosyt9wDT7uCINOIK.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/puerto-rico\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/puerto-rico</a>."}', '2025-09-12 09:40:22', '2025-09-12 09:40:22');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (898, '19_MuSrW1dUQR97MyMZtcA', '67d4b7356d77cefed0f90e35', 'Taiwan', 'tw', 'Western Plains', '<p>The central western plain of Taiwan mainly consists of flat to low rolling plains. It is common to find crops and other farmland in this area.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/taiwan" rel="nofollow" target="_blank">www.plonkit.net/taiwan</a>.', '["https://learnablemeta.com/images/664/Q656V6bpmhyMOAYvfywTzS1biG5hOMWSTiNk.avif"]', '{"country":"Taiwan","metaName":"Western Plains","note":"<p>The central western plain of Taiwan mainly consists of flat to low rolling plains. It is common to find crops and other farmland in this area.</p>","images":["https://learnablemeta.com/images/664/Q656V6bpmhyMOAYvfywTzS1biG5hOMWSTiNk.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/taiwan\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/taiwan</a>."}', '2025-09-12 09:40:54', '2025-09-12 09:40:54');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (900, 'I30xh9SnHvnzosVVNyDWgg', '67d4b7356d77cefed0f90e35', 'Mexico', 'mx', 'Guanajuato Dark Fields', '<p>Extremely dark fields are common in Guanajuato.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/mexico" rel="nofollow" target="_blank">www.plonkit.net/mexico</a>.', '["https://learnablemeta.com/images/664/orflmUL4RyRETUkR017a05w50bsFjQlM58cr.avif"]', '{"country":"Mexico","metaName":"Guanajuato Dark Fields","note":"<p>Extremely dark fields are common in Guanajuato.</p>","images":["https://learnablemeta.com/images/664/orflmUL4RyRETUkR017a05w50bsFjQlM58cr.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/mexico\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/mexico</a>."}', '2025-09-12 09:41:51', '2025-09-12 09:41:51');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (901, 'fqhPAa9z8ryuv_gihGCXTw', '67d4b7356d77cefed0f90e35', 'Chile', 'cl', 'Atacama Mountains', '<p>These rocky, uneven mountains are most commonly seen in Atacama and northern Coquimbo.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/chile" rel="nofollow" target="_blank">www.plonkit.net/chile</a>.', '["https://learnablemeta.com/images/664/eJLLQlFQnQs18qqltU4Je7x8c9Bf2SzmVbna.avif"]', '{"country":"Chile","metaName":"Atacama Mountains","note":"<p>These rocky, uneven mountains are most commonly seen in Atacama and northern Coquimbo.</p>","images":["https://learnablemeta.com/images/664/eJLLQlFQnQs18qqltU4Je7x8c9Bf2SzmVbna.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/chile\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/chile</a>."}', '2025-09-12 09:42:25', '2025-09-12 09:42:25');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (902, 'eYy-OhGKITNrR0vTrmmqbA', '67d4b7356d77cefed0f90e35', 'Canada', 'ca', 'Southern British Columbia', '<p>The landscape in the interior of southern British Columbia, mostly centred around the towns of Kamloops and Kelowna, has a distinct look. It is generally hilly and a little more arid than the rest of BC, The vegetation consists of sprawling brown grasslands, and pine tree forests on the hillsides.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/canada" rel="nofollow" target="_blank">www.plonkit.net/canada</a>.', '["https://learnablemeta.com/images/664/sXuhOya6oF0oIe7cV6CNMDtdtj1tEWuJa0Jd.avif"]', '{"country":"Canada","metaName":"Southern British Columbia","note":"<p>The landscape in the interior of southern British Columbia, mostly centred around the towns of Kamloops and Kelowna, has a distinct look. It is generally hilly and a little more arid than the rest of BC, The vegetation consists of sprawling brown grasslands, and pine tree forests on the hillsides.</p>","images":["https://learnablemeta.com/images/664/sXuhOya6oF0oIe7cV6CNMDtdtj1tEWuJa0Jd.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/canada\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/canada</a>."}', '2025-09-12 09:43:06', '2025-09-12 09:43:06');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (903, 'zk114F03rKeO9VG95UP0AA', '67d4b7356d77cefed0f90e35', 'Ecuador', 'ec', 'Ecuador Dry Coast', '<p>This extremely dry landscape is only found in the coastal region between Guayaquil and Manta.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/ecuador" rel="nofollow" target="_blank">www.plonkit.net/ecuador</a>.', '["https://learnablemeta.com/images/664/EiQkOLwLIDV0XoybiXn9fbSbbuBscoXhh8Ql.avif"]', '{"country":"Ecuador","metaName":"Ecuador Dry Coast","note":"<p>This extremely dry landscape is only found in the coastal region between Guayaquil and Manta.</p>","images":["https://learnablemeta.com/images/664/EiQkOLwLIDV0XoybiXn9fbSbbuBscoXhh8Ql.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/ecuador\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/ecuador</a>."}', '2025-09-12 09:43:29', '2025-09-12 09:43:29');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (904, 'xlcB3VnyqQCtkpypyidGrQ', '67d4b7356d77cefed0f90e35', 'Philippines', 'ph', 'Northern Luzon', '<p>The forested mountain range in Northern Luzon is characterised by somewhat dry mountainsides and an abundance of pine trees. You may rarely come across a similar landscape on high-altitude roads in Mindanao.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/philippines" rel="nofollow" target="_blank">www.plonkit.net/philippines</a>.', '["https://learnablemeta.com/images/664/VkSh1BhkuCeh2WW7jEuyP1vb7vMLrNpnV0Fk.avif"]', '{"country":"Philippines","metaName":"Northern Luzon","note":"<p>The forested mountain range in Northern Luzon is characterised by somewhat dry mountainsides and an abundance of pine trees. You may rarely come across a similar landscape on high-altitude roads in Mindanao.</p>","images":["https://learnablemeta.com/images/664/VkSh1BhkuCeh2WW7jEuyP1vb7vMLrNpnV0Fk.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/philippines\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/philippines</a>."}', '2025-09-12 09:43:58', '2025-09-12 09:43:58');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (905, 'WfWZIwlFtSXLHc_5ZsphRQ', '67d4b7356d77cefed0f90e35', 'Thailand', 'th', 'Nakhon Vibes', '<p>Brown paved roads among fields of sugarcane, cassava, and corn can be found in southeastern Nakhon Ratchasima.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/thailand" rel="nofollow" target="_blank">www.plonkit.net/thailand</a>.', '["https://learnablemeta.com/images/664/jwF7uiTD0CVw6tnQMGnFlbcBpZO9zy3O3VKs.avif"]', '{"country":"Thailand","metaName":"Nakhon Vibes","note":"<p>Brown paved roads among fields of sugarcane, cassava, and corn can be found in southeastern Nakhon Ratchasima.</p>","images":["https://learnablemeta.com/images/664/jwF7uiTD0CVw6tnQMGnFlbcBpZO9zy3O3VKs.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/thailand\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/thailand</a>."}', '2025-09-12 09:44:32', '2025-09-12 09:44:32');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (906, '_ItBvHWFkb83801WJe6N9Q', '67d4b7356d77cefed0f90e35', 'Greece', 'gr', 'Flat Valleys', '<p>The largest flat agricultural valleys in Greece are found close to Thessaloniki and Larissa, but smaller flat valleys, where the mountains generally appear closer, can be found in several other areas in the north and centre of the country.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/greece" rel="nofollow" target="_blank">www.plonkit.net/greece</a>.', '["https://learnablemeta.com/images/664/FVrLHIjOJtTZSc9JTkAqo7nrOJPp1sicCOmN.avif"]', '{"country":"Greece","metaName":"Flat Valleys","note":"<p>The largest flat agricultural valleys in Greece are found close to Thessaloniki and Larissa, but smaller flat valleys, where the mountains generally appear closer, can be found in several other areas in the north and centre of the country.</p>","images":["https://learnablemeta.com/images/664/FVrLHIjOJtTZSc9JTkAqo7nrOJPp1sicCOmN.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/greece\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/greece</a>."}', '2025-09-12 09:45:17', '2025-09-12 09:45:17');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (907, 'xzuNF4J4UkVNbrGprp08tg', '67d4b7356d77cefed0f90e35', 'Botswana', 'bw', 'White Soil', '<p>A large region in the middle western part of the country is characterised by very white sandy soil. In addition, the vegetation tends to look very dry.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (908, 'xmG5Sl_OuTyVigaU32dz0w', '67d4b7356d77cefed0f90e35', 'Russia', 'ru', 'Caspian Sea Desert', '<p>In the desert-like region west of the Caspian Sea, this completely flat and rural Generation 4 coverage on small poorly-tracked dirt roads can only be found in northern Dagestan and southwestern Astrakhan Oblast.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/russia" rel="nofollow" target="_blank">www.plonkit.net/russia</a>.', '["https://learnablemeta.com/images/664/UnLkEkVOISrxr0tgR02mumkLm8knZNFXu59S.avif"]', '{"country":"Russia","metaName":"Caspian Sea Desert","note":"<p>In the desert-like region west of the Caspian Sea, this completely flat and rural Generation 4 coverage on small poorly-tracked dirt roads can only be found in northern Dagestan and southwestern Astrakhan Oblast.</p>","images":["https://learnablemeta.com/images/664/UnLkEkVOISrxr0tgR02mumkLm8knZNFXu59S.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/russia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/russia</a>."}', '2025-09-12 09:47:35', '2025-09-12 09:47:35');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (909, 'iR7rOIZgX_8QKXKloL91Vw', '67d4b7356d77cefed0f90e35', 'Chile', 'cl', 'Maule', '<p>Compared to the area around Santiago, the Maule province has a similar look. However, it has a Mediterranean climate, and is generally greener. Vineyards are also commonly seen in this region.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/chile" rel="nofollow" target="_blank">www.plonkit.net/chile</a>.', '["https://learnablemeta.com/images/664/qirVNVY6P0UdTM7kcLjtKTAFFfqmTbQLmggu.avif"]', '{"country":"Chile","metaName":"Maule","note":"<p>Compared to the area around Santiago, the Maule province has a similar look. However, it has a Mediterranean climate, and is generally greener. Vineyards are also commonly seen in this region.</p>","images":["https://learnablemeta.com/images/664/qirVNVY6P0UdTM7kcLjtKTAFFfqmTbQLmggu.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/chile\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/chile</a>."}', '2025-09-12 09:52:46', '2025-09-12 09:52:46');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (910, '-gt0JwEyUPd2jRm9eYoM1A', '67d4b7356d77cefed0f90e35', 'Chile', 'cl', 'Lauca National Park', '<p>A distinct landscape consisting of a line of snow-capped mountains with two other isolated, symmetrical snow-capped volcanoes are seen in Lauca National Park. Most of the roads are newly paved and have a smooth, dark look.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/chile" rel="nofollow" target="_blank">www.plonkit.net/chile</a>.', '["https://learnablemeta.com/images/664/THAg6N5G2VR1zbPTbX5Luu4vKFmO70qQN0FL.avif"]', '{"country":"Chile","metaName":"Lauca National Park","note":"<p>A distinct landscape consisting of a line of snow-capped mountains with two other isolated, symmetrical snow-capped volcanoes are seen in Lauca National Park. Most of the roads are newly paved and have a smooth, dark look.</p>","images":["https://learnablemeta.com/images/664/THAg6N5G2VR1zbPTbX5Luu4vKFmO70qQN0FL.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/chile\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/chile</a>."}', '2025-09-12 09:53:20', '2025-09-12 09:53:20');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (911, 'oEUh7QhasXjDeL57qQZwLQ', '67d4b7356d77cefed0f90e35', 'Australia', 'au', 'Darwin', '<p>The vegetation in the area surrounding Darwin is extremely distinct. It features dark red soil, as well as rich green grass and trees. The tree trunks are typically black.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/australia" rel="nofollow" target="_blank">www.plonkit.net/australia</a>.', '["https://learnablemeta.com/images/664/JSg5xSSXpaeywECOGVMxpRdBJhfE9TpQFCTt.avif"]', '{"country":"Australia","metaName":"Darwin","note":"<p>The vegetation in the area surrounding Darwin is extremely distinct. It features dark red soil, as well as rich green grass and trees. The tree trunks are typically black.</p>","images":["https://learnablemeta.com/images/664/JSg5xSSXpaeywECOGVMxpRdBJhfE9TpQFCTt.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/australia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/australia</a>."}', '2025-09-12 09:54:18', '2025-09-12 09:54:18');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (912, 'Y3SS5icQZtFC9XWy5vrx3g', '67d4b7356d77cefed0f90e35', 'South Africa', 'za', 'Western Cape Rolling Hills', '<p>Grassy rolling hills are generally located in the coastal area of Western Cape between Cape Town and George, and just north of Cape Town. Tall ridges are usually visible to the north or east.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/south-africa" rel="nofollow" target="_blank">www.plonkit.net/south-africa</a>.', '["https://learnablemeta.com/images/664/y0tgS4CHFpuv75gLzCF2jgFenuewH7OKYitq.avif"]', '{"country":"South Africa","metaName":"Western Cape Rolling Hills","note":"<p>Grassy rolling hills are generally located in the coastal area of Western Cape between Cape Town and George, and just north of Cape Town. Tall ridges are usually visible to the north or east.</p>","images":["https://learnablemeta.com/images/664/y0tgS4CHFpuv75gLzCF2jgFenuewH7OKYitq.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/south-africa\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/south-africa</a>."}', '2025-09-13 12:00:46', '2025-09-13 12:00:46');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (914, 'NGv9yh72_uAJbyAwqcXSDA', '67d4b7356d77cefed0f90e35', 'Kenya', 'ke', 'Mara Triangle', '<p>Mara Triangle has no considerable mountains and most of it is either flat or hilly. It has a white car that can be found in other Kenyan National Parks. Most of the coverage is not within the park, but to the east of it.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/kenya" rel="nofollow" target="_blank">www.plonkit.net/kenya</a>.', '["https://learnablemeta.com/images/664/SJ9KQH9o3HJqI3geql78dALRUsBFG4i8mOIq.avif"]', '{"country":"Kenya","metaName":"Mara Triangle","note":"<p>Mara Triangle has no considerable mountains and most of it is either flat or hilly. It has a white car that can be found in other Kenyan National Parks. Most of the coverage is not within the park, but to the east of it.</p>","images":["https://learnablemeta.com/images/664/SJ9KQH9o3HJqI3geql78dALRUsBFG4i8mOIq.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/kenya\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/kenya</a>."}', '2025-09-13 12:03:55', '2025-09-13 12:03:55');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (915, 'MaqqE7DcL5ZcSNX3sw2VjA', '67d4b7356d77cefed0f90e35', 'United States', 'us', 'Washington Golden Fields', '<p>Agricultural golden fields can commonly be seen in eastern Washington.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (916, '0NRDz87V2bHxahthpeOV8A', '67d4b7356d77cefed0f90e35', 'United States', 'us', 'Florida Low Hills', '<p>The area between the cities of Orlando and Lakeland in Florida sometimes feature low, rolling hills. Lakes and agricultural fields are also common in this area. This is the only main hilly area in central or southern Florida.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/united-states" rel="nofollow" target="_blank">www.plonkit.net/united-states</a>.', '["https://learnablemeta.com/images/664/ivFxoJzxo9oubUXg42Q1So1eHn8t7xAOajZu.avif"]', '{"country":"United States","metaName":"Florida Low Hills","note":"<p>The area between the cities of Orlando and Lakeland in Florida sometimes feature low, rolling hills. Lakes and agricultural fields are also common in this area. This is the only main hilly area in central or southern Florida.</p>","images":["https://learnablemeta.com/images/664/ivFxoJzxo9oubUXg42Q1So1eHn8t7xAOajZu.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/united-states\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/united-states</a>."}', '2025-09-13 12:05:45', '2025-09-13 12:05:45');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (918, 'mxaQYw5oHT9CFnXNhSk8lg', '67d4b7356d77cefed0f90e35', 'Uganda', 'ug', 'Murchison Falls National Park', '<p>The National Park is located by the northern end of Lake Albert and can commonly be recognized by the low dirt barriers along the side of the roads. A savannah can be seen in the western part, while the eastern parts contain many small trees and bushes. Apart from a fairly low and wide mountain range far away to the west, there are no mountains in the vicinity.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/uganda" rel="nofollow" target="_blank">www.plonkit.net/uganda</a>.', '["https://learnablemeta.com/images/664/OeiABFOjQvYQsHM86kKg7Pf2yFzP6Tlb6ytc.avif"]', '{"country":"Uganda","metaName":"Murchison Falls National Park","note":"<p>The National Park is located by the northern end of Lake Albert and can commonly be recognized by the low dirt barriers along the side of the roads. A savannah can be seen in the western part, while the eastern parts contain many small trees and bushes. Apart from a fairly low and wide mountain range far away to the west, there are no mountains in the vicinity.</p>","images":["https://learnablemeta.com/images/664/OeiABFOjQvYQsHM86kKg7Pf2yFzP6Tlb6ytc.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/uganda\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/uganda</a>."}', '2025-09-13 12:07:09', '2025-09-13 12:07:09');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (919, '5QiBXt-kE7QiWd_H6AmGfA', '67d4b7356d77cefed0f90e35', 'Portugal', 'pt', 'Madeira', '<p>Madeira is a very mountainous and semi-tropical island.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/portugal" rel="nofollow" target="_blank">www.plonkit.net/portugal</a>.', '["https://learnablemeta.com/images/664/JnwRIoTIMA5R9gD8tdkLODcIvub0QSyXOXMe.avif"]', '{"country":"Portugal","metaName":"Madeira","note":"<p>Madeira is a very mountainous and semi-tropical island.</p>","images":["https://learnablemeta.com/images/664/JnwRIoTIMA5R9gD8tdkLODcIvub0QSyXOXMe.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/portugal\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/portugal</a>."}', '2025-09-13 12:08:27', '2025-09-13 12:08:27');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (920, '7Zb3cs4p0bnKnfvnLVwWFw', '67d4b7356d77cefed0f90e35', 'United States', 'us', 'Mesa Landscape', '<p>The Southwest, centred around the Four Corners region of Utah, Arizona, New Mexico, and Colorado, feature many mesas and tablelands. Often, these rock formations will be red in colour. Additionally, red soil is common in these areas.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/united-states" rel="nofollow" target="_blank">www.plonkit.net/united-states</a>.', '["https://learnablemeta.com/images/664/SalOyGGZZygIh8Vkjc4LUR1an4nqu3K37RDL.avif"]', '{"country":"United States","metaName":"Mesa Landscape","note":"<p>The Southwest, centred around the Four Corners region of Utah, Arizona, New Mexico, and Colorado, feature many mesas and tablelands. Often, these rock formations will be red in colour. Additionally, red soil is common in these areas.</p>","images":["https://learnablemeta.com/images/664/SalOyGGZZygIh8Vkjc4LUR1an4nqu3K37RDL.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/united-states\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/united-states</a>."}', '2025-09-13 12:09:14', '2025-09-13 12:09:14');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (921, 'wNCA4A7N3GJ6Jm-NiiZzLg', '67d4b7356d77cefed0f90e35', 'Iceland', 'is', 'Barren Volcanic Landscape', '<p>Barren landscapes consisting mostly of dark volcanic gravel, with little to no vegetation or signs of life, are typical of the inland regions of Iceland. Most such covered roads are located in the east.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/iceland" rel="nofollow" target="_blank">www.plonkit.net/iceland</a>.', '["https://learnablemeta.com/images/664/PK5rr0X1jzEJrUqfOTxGkSpmboSbH0pzBKgF.avif"]', '{"country":"Iceland","metaName":"Barren Volcanic Landscape","note":"<p>Barren landscapes consisting mostly of dark volcanic gravel, with little to no vegetation or signs of life, are typical of the inland regions of Iceland. Most such covered roads are located in the east.</p>","images":["https://learnablemeta.com/images/664/PK5rr0X1jzEJrUqfOTxGkSpmboSbH0pzBKgF.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/iceland\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/iceland</a>."}', '2025-09-13 12:09:35', '2025-09-13 12:09:35');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (924, 'JSbrWyeZJDXM6umhhQdZPA', '67d4b7356d77cefed0f90e35', 'Argentina', 'ar', 'San Luis', '<p>Roads with reddish pavement lined with long Argentine needle-grass are common in San Luis.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/argentina" rel="nofollow" target="_blank">www.plonkit.net/argentina</a>.', '["https://learnablemeta.com/images/664/JRvrgPE2plAjOBB76YsRz4uZBpGiEQ95Mv5v.avif"]', '{"country":"Argentina","metaName":"San Luis","note":"<p>Roads with reddish pavement lined with long Argentine needle-grass are common in San Luis.</p>","images":["https://learnablemeta.com/images/664/JRvrgPE2plAjOBB76YsRz4uZBpGiEQ95Mv5v.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/argentina\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/argentina</a>."}', '2025-09-13 12:10:49', '2025-09-13 12:10:49');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (925, 'fDvdHpDpgdASoRV11fpnVw', '67d4b7356d77cefed0f90e35', 'Mexico', 'mx', 'Monterrey Mountains', '<p>The Monterrey metropolitan area features tall stunning mountain ranges that surround the city.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/mexico" rel="nofollow" target="_blank">www.plonkit.net/mexico</a>.', '["https://learnablemeta.com/images/664/l3GLzMAb2Un5LWHv35S7w8PS0Te9SAnfhOWH.avif"]', '{"country":"Mexico","metaName":"Monterrey Mountains","note":"<p>The Monterrey metropolitan area features tall stunning mountain ranges that surround the city.</p>","images":["https://learnablemeta.com/images/664/l3GLzMAb2Un5LWHv35S7w8PS0Te9SAnfhOWH.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/mexico\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/mexico</a>."}', '2025-09-13 12:13:06', '2025-09-13 12:13:06');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (926, 'nXF6MN6xVfu_rFceNiLn_A', '67d4b7356d77cefed0f90e35', 'United States', 'us', 'Central Appalachians', '<p>In the Appalachian area stretching from southern New York to southern Virginia, there are a lot of forest-covered rolling hills, with flat valleys dividing them. A majority of the ridges are oriented Southwest to Northeast.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/united-states" rel="nofollow" target="_blank">www.plonkit.net/united-states</a>.', '["https://learnablemeta.com/images/664/jdydFEdEbQtjNBQ2CCvLJQVYlKtb7B91ljpZ.avif"]', '{"country":"United States","metaName":"Central Appalachians","note":"<p>In the Appalachian area stretching from southern New York to southern Virginia, there are a lot of forest-covered rolling hills, with flat valleys dividing them. A majority of the ridges are oriented Southwest to Northeast.</p>","images":["https://learnablemeta.com/images/664/jdydFEdEbQtjNBQ2CCvLJQVYlKtb7B91ljpZ.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/united-states\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/united-states</a>."}', '2025-09-13 12:13:27', '2025-09-13 12:13:27');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (927, 'KGo4mDXvVw1cg5VCoHxyzQ', '67d4b7356d77cefed0f90e35', 'Canada', 'ca', 'Québec City Windsor Corridor', '<p>The Québec City-Windsor Corridor is the most densely populated part of Canada. It is mostly flat and green, with a patchwork of agriculture and occasional forest cover.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (928, 'ydpAneXnv6jd5WDAb1yzDQ', '67d4b7356d77cefed0f90e35', 'Chile', 'cl', 'Isla Grande de Chiloe', '<p>A rolling, agricultural landscape, comparable to New Zealand, can be seen on Isla Grande de Chiloé.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (929, 'YU5IfzV1DtexeBSKaYiQMQ', '67d4b7356d77cefed0f90e35', 'Taiwan', 'tw', 'Taichung Plains', '<p>Similarly, Taichung features these open plains surrounded by low mountains that are often in the distance.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/taiwan" rel="nofollow" target="_blank">www.plonkit.net/taiwan</a>.', '["https://learnablemeta.com/images/664/2kAIZS7y32HaxqTkhZjsImpz6Lq4ptKH0wSm.avif"]', '{"country":"Taiwan","metaName":"Taichung Plains","note":"<p>Similarly, Taichung features these open plains surrounded by low mountains that are often in the distance.</p>","images":["https://learnablemeta.com/images/664/2kAIZS7y32HaxqTkhZjsImpz6Lq4ptKH0wSm.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/taiwan\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/taiwan</a>."}', '2025-09-13 12:16:26', '2025-09-13 12:16:26');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (930, '92ifEJ2OOJv9PH6OphhXzA', '67d4b7356d77cefed0f90e35', 'Peru', 'pe', 'North Piura Landscape', '<p>Northwest of the city of Piura lies an area with similar vegetation to the area mentioned previously. However, this area is noticeably hillier.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/peru" rel="nofollow" target="_blank">www.plonkit.net/peru</a>.', '["https://learnablemeta.com/images/664/iWJoCH4MlgM64UJcHDQrUu6ql6pcTL7TsAPL.avif"]', '{"country":"Peru","metaName":"North Piura Landscape","note":"<p>Northwest of the city of Piura lies an area with similar vegetation to the area mentioned previously. However, this area is noticeably hillier.</p>","images":["https://learnablemeta.com/images/664/iWJoCH4MlgM64UJcHDQrUu6ql6pcTL7TsAPL.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/peru\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/peru</a>."}', '2025-09-13 12:16:42', '2025-09-13 12:16:42');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (931, 'X_ULtb1W6l2cRBEnIl5AHA', '67d4b7356d77cefed0f90e35', 'Chile', 'cl', 'Llanos des Challe National Park', '<p>Llanos de Challe National Park can be recognized by the brown coloured dirt on the roadside, patches of light sand, juniper plants, and dark coloured mountains. The sky is overcast in most of the coverage.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (932, 'soKz1_ZnGOpW-2oZ5l34sw', '67d4b7356d77cefed0f90e35', 'Sweden', 'se', 'Skåne', '<p>In Skåne you will find the most open agricultural landscape in Sweden. It is the only region where, rather than a dense forest in the distance, you can get only a few small patches of trees strewn across the fields. On top of that, crops are often more diverse than the cereal crops, which are typical for the rest of Sweden.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/sweden" rel="nofollow" target="_blank">www.plonkit.net/sweden</a>.', '["https://learnablemeta.com/images/664/iF7zwBc2icY9KLBxUKkdzmBerG0oFSII3jJL.avif"]', '{"country":"Sweden","metaName":"Skåne","note":"<p>In Skåne you will find the most open agricultural landscape in Sweden. It is the only region where, rather than a dense forest in the distance, you can get only a few small patches of trees strewn across the fields. On top of that, crops are often more diverse than the cereal crops, which are typical for the rest of Sweden.</p>","images":["https://learnablemeta.com/images/664/iF7zwBc2icY9KLBxUKkdzmBerG0oFSII3jJL.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/sweden\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/sweden</a>."}', '2025-09-13 12:18:00', '2025-09-13 12:18:00');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (933, 'glUamYUPM0sZSLflS9GZlA', '67d4b7356d77cefed0f90e35', 'Norway', 'no', 'Farmlands Trondheim', '<p>The farmlands surrounding Trondheim are generally very hilly which are often quite steep</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/norway" rel="nofollow" target="_blank">www.plonkit.net/norway</a>.', '["https://learnablemeta.com/images/664/4PEalZnbwHx3fHFQIvKrd1XJY21QvEkAveoK.avif"]', '{"country":"Norway","metaName":"Farmlands Trondheim","note":"<p>The farmlands surrounding Trondheim are generally very hilly which are often quite steep</p>","images":["https://learnablemeta.com/images/664/4PEalZnbwHx3fHFQIvKrd1XJY21QvEkAveoK.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/norway\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/norway</a>."}', '2025-09-13 12:18:28', '2025-09-13 12:18:28');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (935, 'cH7mpE6KH75raBf1BlRWxQ', '67d4b7356d77cefed0f90e35', 'Ghana', 'gh', 'Eastern Mountains', '<p>Tall mountain ranges in Ghana are typically seen near Lake Volta in the highlighted area.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (936, 'TPy-jlKtErLq1k5cXix4Hw', '67d4b7356d77cefed0f90e35', 'Israel', 'il', 'Jerusalem Hills', '<p>The area west of Jerusalem can often look deceptively similar to northern Israel, with green forested hills and some agriculture sprinkled across the landscape.
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (937, 'HEtD3wTy2hKg0z8lbmlt3Q', '67d4b7356d77cefed0f90e35', 'Taiwan', 'tw', 'Hsinchu Hills', '<p>Miaoli and Hsinchu share a landscape of low rolling hills and winding roads as well as sparse farmland with vegetation that can look messy and unkempt.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/taiwan" rel="nofollow" target="_blank">www.plonkit.net/taiwan</a>.', '["https://learnablemeta.com/images/664/hUlnz45tDqUm2fZ5ys18zvYM0JtQUYB6c2Pg.avif"]', '{"country":"Taiwan","metaName":"Hsinchu Hills","note":"<p>Miaoli and Hsinchu share a landscape of low rolling hills and winding roads as well as sparse farmland with vegetation that can look messy and unkempt.</p>","images":["https://learnablemeta.com/images/664/hUlnz45tDqUm2fZ5ys18zvYM0JtQUYB6c2Pg.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/taiwan\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/taiwan</a>."}', '2025-09-13 12:23:04', '2025-09-13 12:23:04');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (938, 'DVuVEXiZmq-qb5upzO1sQQ', '67d4b7356d77cefed0f90e35', 'Ecuador', 'ec', 'Galapagos Islands', '<p>The Galapagos Islands are an archipelago located west of mainland Ecuador. There are only trekkers and boat coverage on and around the islands. There is a wide variety of landscapes, including volcanic soil, low shrubbery, and craters.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/ecuador" rel="nofollow" target="_blank">www.plonkit.net/ecuador</a>.', '["https://learnablemeta.com/images/664/KtUnXXMmDJ6GKNn0pSGhwvJyo2xGt6ByhrRU.avif"]', '{"country":"Ecuador","metaName":"Galapagos Islands","note":"<p>The Galapagos Islands are an archipelago located west of mainland Ecuador. There are only trekkers and boat coverage on and around the islands. There is a wide variety of landscapes, including volcanic soil, low shrubbery, and craters.</p>","images":["https://learnablemeta.com/images/664/KtUnXXMmDJ6GKNn0pSGhwvJyo2xGt6ByhrRU.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/ecuador\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/ecuador</a>."}', '2025-09-13 12:23:42', '2025-09-13 12:23:42');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (939, '5HxWkISI5ogoesWAM8ojeQ', '67d4b7356d77cefed0f90e35', 'Senegal', 'sn', 'Diourbel', '<p>Diourbel Region is the only region in Senegal with extensive rural coverage. The coverage in the region will look like this, with the Google Car driving on a sandy road with sparse trees. Some of the coverage extends to the west in the Thies region up to Babou.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (940, 'RoIp_oamEH22-Tjnk9BRvg', '67d4b7356d77cefed0f90e35', 'Czechia', 'cz', 'North Hills', '<p>Much of the northern Bohemian landscape can be characterised by a rolling landscape combined with steep solitary hills.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (941, '9jEQh5uMq2dMvm5ASudCQA', '67d4b7356d77cefed0f90e35', 'Chile', 'cl', 'Los Flamencos National Reserve', '<p>Smooth mountains with a reddish tint are seen in Los Flamencos National Reserve. Salt pans are also a common sight. The reserve uses triple yellow lines, with short dashes in the west.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/chile" rel="nofollow" target="_blank">www.plonkit.net/chile</a>.', '["https://learnablemeta.com/images/664/VSRnU2ZXZp1x0sKvkOZZn6RJEOCYw7pyRxGk.avif"]', '{"country":"Chile","metaName":"Los Flamencos National Reserve","note":"<p>Smooth mountains with a reddish tint are seen in Los Flamencos National Reserve. Salt pans are also a common sight. The reserve uses triple yellow lines, with short dashes in the west.</p>","images":["https://learnablemeta.com/images/664/VSRnU2ZXZp1x0sKvkOZZn6RJEOCYw7pyRxGk.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/chile\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/chile</a>."}', '2025-09-13 12:25:05', '2025-09-13 12:25:05');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (942, 'AWnJO5f2JuJuo-ri37grlQ', '67d4b7356d77cefed0f90e35', 'Israel', 'il', 'North', '<p>Northern Israel is usually far more mountainous and green than the rest of Israel. Throughout the region you will almost always see forested hills and mountains.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/israel-west-bank" rel="nofollow" target="_blank">www.plonkit.net/israel-west-bank</a>.', '["https://learnablemeta.com/images/664/IhzaEsQtKH8vY2nuBSZCWiWTK4Uqd6UPZ8D8.avif"]', '{"country":"Israel","metaName":"North","note":"<p>Northern Israel is usually far more mountainous and green than the rest of Israel. Throughout the region you will almost always see forested hills and mountains.</p>","images":["https://learnablemeta.com/images/664/IhzaEsQtKH8vY2nuBSZCWiWTK4Uqd6UPZ8D8.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/israel-west-bank\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/israel-west-bank</a>."}', '2025-09-13 12:25:55', '2025-09-13 12:25:55');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (943, '6ENv_K_4XG810pG-VBKxLg', '67d4b7356d77cefed0f90e35', 'Netherlands', 'nl', 'Polder', '<p>Most of the west and north is at or below sea-level. As a result, most of this region’s countryside has a distinct “polder” landscape: the landscape is marshy, often with a large amount of small waterways.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/netherlands" rel="nofollow" target="_blank">www.plonkit.net/netherlands</a>.', '["https://learnablemeta.com/images/664/JJk2YoUoeJelHinOKysuO68RPS0046X8u0Ie.avif"]', '{"country":"Netherlands","metaName":"Polder","note":"<p>Most of the west and north is at or below sea-level. As a result, most of this region’s countryside has a distinct “polder” landscape: the landscape is marshy, often with a large amount of small waterways.</p>","images":["https://learnablemeta.com/images/664/JJk2YoUoeJelHinOKysuO68RPS0046X8u0Ie.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/netherlands\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/netherlands</a>."}', '2025-09-13 12:26:19', '2025-09-13 12:26:19');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (944, 'XvI7l_hLjU-J-W1fSKoH1g', '67d4b7356d77cefed0f90e35', 'Brazil', 'br', 'Coastal Rio Grande do Sul', '<p>Coastal regions in Rio Grande do Sul are recognisable due to being a rare flat wetland, which is uncommon in Brazil.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/brazil" rel="nofollow" target="_blank">www.plonkit.net/brazil</a>.', '["https://learnablemeta.com/images/664/59onDn8pEP4VA4WsdV3FF0JFu1QlUNtC8NBk.avif"]', '{"country":"Brazil","metaName":"Coastal Rio Grande do Sul","note":"<p>Coastal regions in Rio Grande do Sul are recognisable due to being a rare flat wetland, which is uncommon in Brazil.</p>","images":["https://learnablemeta.com/images/664/59onDn8pEP4VA4WsdV3FF0JFu1QlUNtC8NBk.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/brazil\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/brazil</a>."}', '2025-09-13 12:27:08', '2025-09-13 12:27:08');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (945, 'LdSD6k8ob3hgyT3I8R5atw', '67d4b7356d77cefed0f90e35', 'Israel', 'il', 'Mount Carmel', '<p>Mount Carmel is a long mountain ridge, going northwest to southeast, near the city of Haifa. From the northeast, it can easily be recognised by its direction and size.
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (947, '4mq8728_c6s59i7cLKmLnA', '67d4b7356d77cefed0f90e35', 'Italy', 'it', 'Etna', '<p>Etna, the tallest volcano in Europe, can be seen rising above the landscape from far away in eastern Sicily.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/italy" rel="nofollow" target="_blank">www.plonkit.net/italy</a>.', '["https://learnablemeta.com/images/664/QTyfwXdfV2nJZeAFh2l0lNGw51Tri0zetUW7.avif"]', '{"country":"Italy","metaName":"Etna","note":"<p>Etna, the tallest volcano in Europe, can be seen rising above the landscape from far away in eastern Sicily.</p>","images":["https://learnablemeta.com/images/664/QTyfwXdfV2nJZeAFh2l0lNGw51Tri0zetUW7.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/italy\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/italy</a>."}', '2025-09-13 12:29:00', '2025-09-13 12:29:00');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (948, 'vEO3VcyACuAuKoYx3zXGHA', '67d4b7356d77cefed0f90e35', 'Brazil', 'br', 'Northeast Landscape', '<p>Northeastern Brazil can be identified by its dry soil, shrubs and succulents (the Caatinga landscape). In rainy season coverage, the shrubs are still recognisable, but can be deceptively green.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/brazil" rel="nofollow" target="_blank">www.plonkit.net/brazil</a>.', '["https://learnablemeta.com/images/664/tqHBIrqNuPgT1SfI01cA2KaDGDaMlC6Es6BR.avif"]', '{"country":"Brazil","metaName":"Northeast Landscape","note":"<p>Northeastern Brazil can be identified by its dry soil, shrubs and succulents (the Caatinga landscape). In rainy season coverage, the shrubs are still recognisable, but can be deceptively green.</p>","images":["https://learnablemeta.com/images/664/tqHBIrqNuPgT1SfI01cA2KaDGDaMlC6Es6BR.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/brazil\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/brazil</a>."}', '2025-09-13 12:29:24', '2025-09-13 12:29:24');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (949, 'P080uxu_PVQ5Eg3L47A_hg', '67d4b7356d77cefed0f90e35', 'Montenegro', 'me', 'Dry Mountains', '<p>Dry mountains with a lack of trees are seen around Podgorica, and to the south of it. Vineyards are also commonly seen in this region.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/montenegro" rel="nofollow" target="_blank">www.plonkit.net/montenegro</a>.', '["https://learnablemeta.com/images/664/0L2mhjHFostU4OdHlj7uBh3NNFsRK1ETHqJL.avif"]', '{"country":"Montenegro","metaName":"Dry Mountains","note":"<p>Dry mountains with a lack of trees are seen around Podgorica, and to the south of it. Vineyards are also commonly seen in this region.</p>","images":["https://learnablemeta.com/images/664/0L2mhjHFostU4OdHlj7uBh3NNFsRK1ETHqJL.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/montenegro\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/montenegro</a>."}', '2025-09-13 12:30:15', '2025-09-13 12:30:15');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (950, 'v1Xosjwo6RIQROtD4bv57g', '67d4b7356d77cefed0f90e35', 'Mongolia', 'mn', 'Southwestern Steppe', '<p>You can find arid steppe rangeland, characterised by the coarse soil, patchy grass, and small shrubs, throughout southwestern Mongolia.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/mongolia" rel="nofollow" target="_blank">www.plonkit.net/mongolia</a>.', '["https://learnablemeta.com/images/664/ORHWn4QNXynd5u394wGPKQv8CdryyGuYZa0P.avif"]', '{"country":"Mongolia","metaName":"Southwestern Steppe","note":"<p>You can find arid steppe rangeland, characterised by the coarse soil, patchy grass, and small shrubs, throughout southwestern Mongolia.</p>","images":["https://learnablemeta.com/images/664/ORHWn4QNXynd5u394wGPKQv8CdryyGuYZa0P.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/mongolia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/mongolia</a>."}', '2025-09-13 12:30:34', '2025-09-13 12:30:34');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (951, 'oSUfeUPsotS--hRzrGitnA', '67d4b7356d77cefed0f90e35', 'Guatemala', 'gt', 'North', '<p>The northern regions of Guatemala are typically fairly flat. You will however usually see some hills or a slightly rolling landscape, rather than the complete flatness of the south.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/guatemala" rel="nofollow" target="_blank">www.plonkit.net/guatemala</a>.', '["https://learnablemeta.com/images/664/b0xzCggLla2oIDoAYESXVrnuagIimLQHlOkf.avif"]', '{"country":"Guatemala","metaName":"North","note":"<p>The northern regions of Guatemala are typically fairly flat. You will however usually see some hills or a slightly rolling landscape, rather than the complete flatness of the south.</p>","images":["https://learnablemeta.com/images/664/b0xzCggLla2oIDoAYESXVrnuagIimLQHlOkf.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/guatemala\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/guatemala</a>."}', '2025-09-13 12:30:53', '2025-09-13 12:30:53');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (954, 'nCwTHEbCypEEHYneKDCWqg', '67d4b7356d77cefed0f90e35', 'Serbia', 'rs', 'Northern Plains', '<p>Vojvodina, the northern region between the Hungarian border and Belgrade, can be recognized by the flat, agricultural landscape. The landscape will be completely flat with no hills whatsoever.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/serbia" rel="nofollow" target="_blank">www.plonkit.net/serbia</a>.', '["https://learnablemeta.com/images/664/hdGwU2WCn4reyHI9cmWon4zXEGLaiyRDT3EY.avif"]', '{"country":"Serbia","metaName":"Northern Plains","note":"<p>Vojvodina, the northern region between the Hungarian border and Belgrade, can be recognized by the flat, agricultural landscape. The landscape will be completely flat with no hills whatsoever.</p>","images":["https://learnablemeta.com/images/664/hdGwU2WCn4reyHI9cmWon4zXEGLaiyRDT3EY.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/serbia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/serbia</a>."}', '2025-09-13 12:32:22', '2025-09-13 12:32:22');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (955, 'E7dcuKFKaZ1GtCcSzBmONg', '67d4b7356d77cefed0f90e35', 'United States', 'us', 'Lānaʻi Coastal Hills', '<p>Towards the coasts of the island of Lānaʻi, hills dotted with dry grass and shrubby vegetation are commonly seen. These areas are very rural with few buildings.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/united-states" rel="nofollow" target="_blank">www.plonkit.net/united-states</a>.', '["https://learnablemeta.com/images/664/ZReeOSA9uwOagHdLQeN9EKnHoRLPS11sLtwl.avif"]', '{"country":"United States","metaName":"Lānaʻi Coastal Hills","note":"<p>Towards the coasts of the island of Lānaʻi, hills dotted with dry grass and shrubby vegetation are commonly seen. These areas are very rural with few buildings.</p>","images":["https://learnablemeta.com/images/664/ZReeOSA9uwOagHdLQeN9EKnHoRLPS11sLtwl.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/united-states\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/united-states</a>."}', '2025-09-13 12:32:49', '2025-09-13 12:32:49');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (956, '4n2HwPam7nL9KKZkZ4dClA', '66c0d3feff4dbe492e06174e', 'Reunion', 're', 'Unique car', '<p>Réunion''s Street View car features visible side mirrors, a roof rack, and black tape at the end of the rear right bar.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (961, 'oU9zcI_znI33zadMgxPvEg', '66c0d3feff4dbe492e06174e', 'South Korea', 'kr', 'Language', '<p>Korean script is highly distinctive, featuring a combination of circular shapes and blocky, straight-line characters.</p>', 'Check out  <a href="https://www.plonkit.net/south-korea" rel="nofollow" target="_blank">www.plonkit.net/south-korea</a> for more clues.', '["https://learnablemeta.com/images/JRlq3b42zTeq2gt6rkCgg2TJwhcvmsikB8SL.avif"]', '{"country":"South Korea","metaName":"Language","note":"<p>Korean script is highly distinctive, featuring a combination of circular shapes and blocky, straight-line characters.</p>","images":["https://learnablemeta.com/images/JRlq3b42zTeq2gt6rkCgg2TJwhcvmsikB8SL.avif"],"footer":"Check out  <a href=\"https://www.plonkit.net/south-korea\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/south-korea</a> for more clues."}', '2025-09-15 13:49:47', '2025-09-15 13:49:47');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (964, 'Lo_bbGTZmkDIh-uFZfVQpg', '66c0d3feff4dbe492e06174e', 'Botswana', 'bw', 'Vibes', '<p>A dry, flat landscape with African vegetation, usually a bit away from the road, is a good clue for Botswana and becomes quite distinct after seeing it couple times.
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (966, 'psyghFyE64TYyfOp_zY-PA', '66c0d3feff4dbe492e06174e', 'Thailand', 'th', 'Pole', '<p>Square poles with small holes running from bottom to top are distinctive to Thailand. In Sri Lanka, similar poles exist, but the holes are limited to the top part of the pole.</p>
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (967, 'uwWjhmkzUzdQlZ1NONCqrA', '66c0d3feff4dbe492e06174e', 'United States', 'us', 'No passing zone sign', '<p>Yellow triangular signs with the text "No Passing Zone" are unique to the United States.</p>', 'Check out  <a href="https://www.plonkit.net/united-states" rel="nofollow" target="_blank">www.plonkit.net/united-states</a> for more clues.', '["https://learnablemeta.com/images/O2HKvzlW6FolA1RfIVJyP2I0g0ahNIJUlcn3.avif"]', '{"country":"United States","metaName":"No passing zone sign","note":"<p>Yellow triangular signs with the text \"No Passing Zone\" are unique to the United States.</p>","images":["https://learnablemeta.com/images/O2HKvzlW6FolA1RfIVJyP2I0g0ahNIJUlcn3.avif"],"footer":"Check out  <a href=\"https://www.plonkit.net/united-states\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/united-states</a> for more clues."}', '2025-09-15 13:54:41', '2025-09-15 13:54:41');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (970, 'UaEM4je0lQy_vcNL1un-_Q', '66c0d3feff4dbe492e06174e', 'Malaysia', 'my', 'Double white middle line', '<p>Malaysia frequently uses double white center lines on its two-lane roads, making it the only Southeast Asian country to do so.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/malaysia" rel="nofollow" target="_blank">www.plonkit.net/malaysia</a>.', '["https://learnablemeta.com/images/MiPYSaQNwqprHBpMzAIMGj8sL16I39IZ92gB.avif"]', '{"country":"Malaysia","metaName":"Double white middle line","note":"<p>Malaysia frequently uses double white center lines on its two-lane roads, making it the only Southeast Asian country to do so.</p>","images":["https://learnablemeta.com/images/MiPYSaQNwqprHBpMzAIMGj8sL16I39IZ92gB.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/malaysia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/malaysia</a>."}', '2025-09-15 13:56:17', '2025-09-15 13:56:17');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (971, '5dox_aCykhu5p4nXE5B2hQ', '66c0d3feff4dbe492e06174e', 'South Korea', 'kr', 'Pole marking', '<p>Black and yellow diagonal stripes on poles are common in South Korea. You might confuse them with Taiwanese ones, but in South Korea, the stripes never go all the way to the bottom, while the ones in Taiwan usually do and have more of an orange tint to them.</p>', 'Check out  <a href="https://www.plonkit.net/south-korea" rel="nofollow" target="_blank">www.plonkit.net/south-korea</a> for more clues.', '["https://learnablemeta.com/images/7dTk9F6SzOmyF7JejQtwFWISpOCXQDkGzCKG.avif"]', '{"country":"South Korea","metaName":"Pole marking","note":"<p>Black and yellow diagonal stripes on poles are common in South Korea. You might confuse them with Taiwanese ones, but in South Korea, the stripes never go all the way to the bottom, while the ones in Taiwan usually do and have more of an orange tint to them.</p>","images":["https://learnablemeta.com/images/7dTk9F6SzOmyF7JejQtwFWISpOCXQDkGzCKG.avif"],"footer":"Check out  <a href=\"https://www.plonkit.net/south-korea\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/south-korea</a> for more clues."}', '2025-09-15 13:56:59', '2025-09-15 13:56:59');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (976, '1EV3Xpn0fAm9Fn3GRBSS1g', '66c0d3feff4dbe492e06174e', 'United Arab Emirates', 'ae', 'Silver car', '<p>If you have vibes/other clues that you are in the middle east and you have silver car in Gen3 that means you can only be in UAE.</p>', 'Check out  <a href="https://www.plonkit.net/united-arab-emirates" rel="nofollow" target="_blank">www.plonkit.net/united-arab-emirates</a> for more clues.', '["https://learnablemeta.com/images/1/OjkNdFimedIHF5z9pLAKWe72MvJ8xTuB0wjP.avif"]', '{"country":"United Arab Emirates","metaName":"Silver car","note":"<p>If you have vibes/other clues that you are in the middle east and you have silver car in Gen3 that means you can only be in UAE.</p>","images":["https://learnablemeta.com/images/1/OjkNdFimedIHF5z9pLAKWe72MvJ8xTuB0wjP.avif"],"footer":"Check out  <a href=\"https://www.plonkit.net/united-arab-emirates\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/united-arab-emirates</a> for more clues."}', '2025-09-15 14:24:35', '2025-09-15 14:24:35');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (977, '4lpxzh2Ew0ZnGfQTnFNYvw', '67d4b7356d77cefed0f90e35', 'Colombia', 'co', 'East of Andes Lowlands', '<p>East of the Andes lies a vast lowland called Llanos. It is characterised by open tropical grasslands, savannas with scattered trees, small areas of forest and seasonal flooding.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/colombia" rel="nofollow" target="_blank">www.plonkit.net/colombia</a>.', '["https://learnablemeta.com/images/664/OJF6hLAzl5DNV2qRxUjXYfD2MODBKPWF0ubJ.avif"]', '{"country":"Colombia","metaName":"East of Andes Lowlands","note":"<p>East of the Andes lies a vast lowland called Llanos. It is characterised by open tropical grasslands, savannas with scattered trees, small areas of forest and seasonal flooding.</p>","images":["https://learnablemeta.com/images/664/OJF6hLAzl5DNV2qRxUjXYfD2MODBKPWF0ubJ.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/colombia\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/colombia</a>."}', '2025-09-24 05:04:19', '2025-09-24 05:04:19');
INSERT INTO locations (id, pano_id, map_id, country, country_code, meta_name, note, footer, images, raw_data, created_at, updated_at) VALUES (978, 'yIO1je5N8c-EVxeBdFqE5A', '67d4b7356d77cefed0f90e35', 'Mexico', 'mx', 'North Baja California Hills', '<p>These reddish brown-tinged hills, often accompanied by large boulders, are a common sight for the northern half of Baja California.</p>', 'Description and images taken from:  <a href="https://www.plonkit.net/mexico" rel="nofollow" target="_blank">www.plonkit.net/mexico</a>.', '["https://learnablemeta.com/images/664/H4yW2wS7oDfdTmh5M6uukudMpz24HNUgJa6l.avif"]', '{"country":"Mexico","metaName":"North Baja California Hills","note":"<p>These reddish brown-tinged hills, often accompanied by large boulders, are a common sight for the northern half of Baja California.</p>","images":["https://learnablemeta.com/images/664/H4yW2wS7oDfdTmh5M6uukudMpz24HNUgJa6l.avif"],"footer":"Description and images taken from:  <a href=\"https://www.plonkit.net/mexico\" rel=\"nofollow\" target=\"_blank\">www.plonkit.net/mexico</a>."}', '2025-09-24 05:10:05', '2025-09-24 05:10:05');
